import { setSelectionRange } from '@testing-library/user-event/dist/utils';
import { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './App.css';

function App() {
  const [date, setDate] = useState(new Date());
  const fromdt1= new Date("2022-01-08");
  const todt1= new Date("2022-04-03");
  const fromdt2= new Date("2022-04-04");
  const todt2= new Date("2022-04-15");
  const fromdt3= new Date("2022-04-16");
  const todt3= new Date("2022-04-22");
  const fromdt4= new Date("2022-04-23");
  const todt4= new Date("2022-05-07");
  const fromdt5= new Date("2022-05-08");
  const todt5= new Date("2022-05-21");
  const fromdt6= new Date("2022-06-12");
  const todt6= new Date("2022-06-26");
  const fromdt7= new Date("2022-06-12");
  const todt7= new Date("2022-06-26");
  const fromdt8= new Date("2022-06-27");
  const todt8= new Date("2022-07-01");
  const fromdt9= new Date("2022-07-02");
  const todt9= new Date("2022-07-13");


  return (
    <div className='app'>
      <h1 className='text-center'>Where The F@ck is Ryan?</h1>
      <div className='calendar-container'>
        <Calendar
          tileContent={({ date, view }) => view === 'month' && date >= fromdt1 && date <= todt1 ? <p><img id="x" src="https://icons.iconarchive.com/icons/custom-icon-design/all-country-flag/32/Peru-Flag-icon.png" alt="Peru!"/></p> : null}
          // onChange={setDate}
          // value={date}
          selectRange={true}
        />
      </div>
      {date.length > 0 ? (
        <p className='text-center'>
          <span className='bold'>Start:</span>{' '}
          {date[0].toDateString()}
          &nbsp;|&nbsp;
          <span className='bold'>End:</span> {date[1].toDateString()}
        </p>
      ) : (
        <p className='text-center'>
          <span className='bold'>Default selected date:</span>{' '}
          {date.toDateString()}
        </p>
      )}
    </div>
  );
}

export default App;
